import React, { useState, useEffect, useCallback, useRef } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const API_URL = "http://localhost:5000/api"; // Centralized API URL

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]);
  const [voteSuccess, setVoteSuccess] = useState("");
  const [error, setError] = useState("");
  const [hasVoted, setHasVoted] = useState(false);
  const [isVoting, setIsVoting] = useState(false);

  const isMounted = useRef(true); // Track if component is mounted

  useEffect(() => {
    isMounted.current = true;
    return () => {
      isMounted.current = false; // Cleanup on unmount
    };
  }, []);

  /** ✅ Check if voter has already voted */
  const checkVoterStatus = useCallback(async () => {
    if (!voter_id) {
      setError("❌ Voter ID is required.");
      return;
    }
    try {
      console.log("🔍 Checking voter status for:", voter_id);
      const res = await axios.post(`http://localhost:5000/api/check-voter`, { voter_id });

      console.log("✅ Voter check response:", res.data);
      if (!res.data.success) {
        if (isMounted.current) {
          setHasVoted(true);
          setVoteSuccess(res.data.message);
        }
      }
    } catch (error) {
      console.error("❌ Error checking voter status:", error?.response?.data || error);
      if (isMounted.current) {
        setError(error.response?.data?.message || "❌ Failed to check voter status.");
      }
    }
  }, [voter_id]);

  /** ✅ Fetch available candidates */
  const fetchCandidates = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/candidate`);
      console.log("✅ Fetched Candidates:", res.data);
      if (isMounted.current) {
        setCandidates(res.data || []);
      }
    } catch (error) {
      console.error("❌ Error fetching candidates:", error);
      if (isMounted.current) {
        setError("❌ Failed to load candidates. Please refresh.");
      }
    }
  }, []);

  useEffect(() => {
    if (voter_id) {
      checkVoterStatus();
      fetchCandidates();
    }
  }, [voter_id, checkVoterStatus, fetchCandidates]);

  /** ✅ Handle Vote Submission */
  const handleVote = async (candidateId) => {
    if (!voter_id) {
      setError("❌ Voter ID is required to vote.");
      return;
    }
    if (isVoting || hasVoted) return;
    setIsVoting(true);
    try {
      console.log("🗳️ Voting for:", candidateId);
      const res = await axios.post(`${API_URL}/vote`, { voter_id, candidateId });

      if (res.data.success) {
        if (isMounted.current) {
          setVoteSuccess("✅ Your vote has been recorded!");
          setHasVoted(true);
        }
      } else {
        if (isMounted.current) {
          setError(res.data.message || "❌ Unable to cast vote.");
        }
      }
    } catch (error) {
      console.error("❌ Error casting vote:", error);
      if (isMounted.current) {
        setError(error.response?.data?.message || "❌ Error casting vote. Please try again.");
      }
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">🗳️ Online Voting System</h1>

      {error && <p className="error-message">{error}</p>}
      {voteSuccess && <p className="success-message">{voteSuccess}</p>}

      {hasVoted ? (
        <p className="already-voted">⚠️ Thanks for voting! You cannot vote again.</p>
      ) : (
        <div className="candidate-list">
          {candidates.length > 0 ? (
            candidates.map((candidate) => (
              <div key={candidate._id} className="candidate-row">
                <div className="candidate-info">
                  <img
                    src={`${API_URL}/uploads/${candidate.logo}`}
                    alt={candidate.name}
                    className="candidate-logo"
                  />
                  <p className="candidate-name">{candidate.name}</p>
                  <p className="vote-count">Votes: {candidate.votes ?? 0}</p>
                  <button
                    className="vote-button"
                    onClick={() => handleVote(candidate._id)}
                    disabled={isVoting}
                  >
                    {isVoting ? "Voting..." : "🗳️ Vote"}
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p className="no-candidates">⚠️ No candidates available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
