  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const voterId = "456789123025"; // âœ… Hardcoded for testing

  // âœ… Use useCallback to memoize verifyVoter function
  const verifyVoter = useCallback(async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/voter/check-vote", { voterId });

      if (response.data.success) {
        if (response.data.message.includes("Thanks for voting")) {
          setHasVoted(true);
        } else {
          setIsVerified(true);
          fetchCandidates();
        }
      } else {
        alert("âŒ Invalid Voter ID.");
      }
    } catch (error) {
      console.error("âŒ Error verifying voter:", error);
      alert("Error verifying voter. Check server logs.");
    }
  }, [voterId]); // âœ… Add voterId as a dependency

  useEffect(() => {
    verifyVoter();
  }, [verifyVoter]); // âœ… Add verifyVoter to dependency array

  return (
    <div className="voter-dashboard">
      <h2>ðŸ—³ Voter Dashboard</h2>
      {hasVoted ? (
        <h3>âœ… Thanks for Voting!</h3>
      ) : isVerified ? (
        <p>You can vote now!</p>
      ) : (
        <p>Verifying Voter ID...</p>
      )}
    </div>
  );
};

export default VoterDashboard;
